import axios from "axios";
import apiKeys from "../apiKeys";

const firebaseUrl = apiKeys.apiKeys.firebaseConfig.databaseURL;

const addCollectionItem = (newItem) =>
  axios.post(`${firebaseUrl}/collection.json`, newItem);

const deleteFromCollection = (messageId) =>
  axios.delete(`${firebaseUrl}/collection/${messageId}.json`);

export default { addCollectionItem, deleteFromCollection };
