import React, { Component } from "react";
import "./DetailsPage.scss";
import contentShape from "./../../Helpers/PropShapes/contentShape";
import data from "../../Helpers/Data/comicData.json";

export class DetailsPage extends Component {
  state = {
    contentItem: contentShape,
    popular: data.Popular,
    movies: data.Movies,
    comics: data.Comics,
    series: data.Series,
  };

  componentDidMount() {
    const { popular, movies, comics, series } = this.state;
    const contentId = this.props.match.params.id;
    const getContentById = (id) => {
      id = contentId;
      let contentArray = [...popular, ...movies, ...comics, ...series];
      let contentItem = contentArray.filter((item) => item.id === id);
      this.setState({
        contentItem: contentItem,
      });
    };

    getContentById();
  }

  render() {
    const { contentItem } = this.state;
    return (
      <div>
        <div id="content-image-div">
          <div className="header">
            <h2>DetailsPage works!</h2>
          </div>
          <div>
            <h2>RIGHT HERE</h2>
            <h2>{contentItem.name}</h2>
            <img src={contentItem.image_src} alt={contentItem.name} />
          </div>
        </div>
      </div>
    );
  }
}

export default DetailsPage;
