import firebase from "firebase/app";
import "firebase/auth";
import apiKeys from "./apiKeys";

const firebaseApp = () => {
  if (!firebase.apps.length) {
    firebase.initializeApp(apiKeys.apiKeys.firebaseConfig);
  }
};

export default firebaseApp;
