import React, { Component } from "react";
import "./DetailsPage.scss";
import contentShape from "./../../Helpers/PropShapes/contentShape";
import data from "../../Helpers/Data/comicData.json";

export class DetailsPage extends Component {
  state = {
    contentItem: contentShape,
    popular: data.Popular,
    movies: data.Movies,
    comics: data.Comics,
    series: data.Series,
  };

  componentDidMount() {
    const { popular, movies, comics, series } = this.state;
    const contentId = this.props.match.params.id;
    const getContentById = (id) => {
      id = contentId;
      let contentArray = [...popular, ...movies, ...comics, ...series];
      let contentItem = contentArray.filter((item) => item.id === id)[0];
      this.setState({
        contentItem: contentItem,
      });
    };

    getContentById();
  }

  render() {
    const { contentItem } = this.state;
    return (
      <div className="detail-page-container">
        <div className="top-area">
          <div id="first-column">
            <h2>{contentItem.name}</h2>
            <img
              className="content-image"
              src={contentItem.image_src}
              alt={contentItem.name}
            />
            <button type="button" className="add-to-list-btn btn btn-primary">
              Add to Collection
            </button>
          </div>
          <div id="second-column">
            <div className="video-header-div">
              <h2>Video</h2>
            </div>
            {/* <section className="video-section"></section> */}
            <img
              className="video-player"
              src={contentItem.image_src}
              alt={contentItem.name}
            />
            <div className="details-header-div">
              <h2>Details</h2>
            </div>
            <div className="details-first-column">
              <p>
                Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit
                illum ducimus sapiente eaque! Odit fugiat nemo odio ex expedita
                tempora velit magnam in reiciendis dolore!
              </p>
            </div>
            <div className="details-second-column">
              <p>
                Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit
                illum ducimus sapiente eaque! Odit fugiat nemo odio ex expedita
                tempora velit magnam in reiciendis dolore!
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default DetailsPage;
