import React from "react";
import "./MyNavbar.scss";
import PropTypes from "prop-types";
// import MenuItem from "@material-ui/core/MenuItem";
// import Select from "@material-ui/core/Select";
import { Link } from "react-router-dom";
import Drawer from "@material-ui/core/Drawer";
import Divider from "@material-ui/core/Divider";
import List from "@material-ui/core/List";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import VisibilityIcon from "@material-ui/icons/Visibility";
import ChatIcon from "@material-ui/icons/Chat";
import NotificationsIcon from "@material-ui/icons/Notifications";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
// import LoginModal from "../Modals/LoginModal/LoginModal";
import Button from "@material-ui/core/Button";

class MyNavbar extends React {
  static propTypes = {
    isAuthed: PropTypes.bool,
    logoutClickEvent: PropTypes.func,
  };

  state = {
    drawerOpen: false,
    modalOpen: false,
    loggingIn: false,
  };

  toggleDrawer = () => {
    this.setState({ drawerOpen: !this.state.drawerOpen });
  };

  showModal = () => {
    this.setState({ modalOpen: true });
    if (!!this.state.drawerOpen) {
      this.toggleDrawer();
    }
  };

  hideModal = () => {
    this.setState({ modalOpen: false });
    console.log("Yup.. this is the function being called..");
  };

  logOut = () => {
    this.props.logoutClickEvent();
    this.toggleDrawer();
  };
  buildNavBar = () => {
    const { isAuthed } = this.props;
    if (isAuthed) {
      return (
        <div className="nav-group">
          <div className="sell-button">
            <Button className="icon-and-sell">
              <span className="material-icons camera-icon">camera_alt</span>
              Sell
            </Button>
          </div>
          <Link to="/chat" className="material-icons chat-icon">
            sms
          </Link>
          <Link to="/notifications" className="material-icons bell-icon">
            notifications
          </Link>
          <img
            src="https://s3-us-east-2.amazonaws.com/redefined/wp-content/uploads/2019/11/12161154/sonic_paramount.jpg"
            alt="Sonic Profile Pic"
            className="profile-pic"
            onClick={this.toggleDrawer}
          ></img>
          <Drawer
            className="drawer-component"
            anchor="right"
            open={this.state.drawerOpen}
            onClose={this.toggleDrawer}
            variant="temporary"
          >
            <div className="drawer-profile-section">
              <img
                src="https://s3-us-east-2.amazonaws.com/redefined/wp-content/uploads/2019/11/12161154/sonic_paramount.jpg"
                alt="Sonic Profile Pic"
                className="drawer-profile-pic"
              ></img>
              <h2>{this.props.userObject.fullName}</h2>
              <h4>Green Hills</h4>
            </div>
            <Divider />
            <List>
              {[
                <ListItem
                  button
                  onClick={this.toggleDrawer}
                  component={Link}
                  to="/home"
                  key={"Discover"}
                >
                  <ListItemIcon>{<VisibilityIcon />}</ListItemIcon>
                  <ListItemText primary={"Discover"} />
                </ListItem>,

                <ListItem
                  button
                  onClick={this.toggleDrawer}
                  component={Link}
                  to="/chat"
                  key={"Chat"}
                >
                  <ListItemIcon>{<ChatIcon />}</ListItemIcon>
                  <ListItemText primary={"Chat"} />
                </ListItem>,

                <ListItem
                  button
                  component={Link}
                  onClick={this.toggleDrawer}
                  to="/notifications"
                  key={"Notifications"}
                >
                  <ListItemIcon>{<NotificationsIcon />}</ListItemIcon>
                  <ListItemText primary={"Notifications"} />
                </ListItem>,

                <ListItem
                  button
                  onClick={this.toggleDrawer}
                  component={Link}
                  to="/profile"
                  key={"Profile"}
                >
                  <ListItemIcon>{<AccountCircleIcon />}</ListItemIcon>
                  <ListItemText primary={"Profile"} />
                </ListItem>,

                <ListItem button onClick={this.logOut} key={"Log Out"}>
                  <ListItemIcon>{<ExitToAppIcon />}</ListItemIcon>
                  <ListItemText primary={"Log Out"} />
                </ListItem>,
              ]}
            </List>
          </Drawer>
        </div>
      );
    } else {
      return (
        <div className="nav-group">
          <div className="sell-button">
            <Button className="icon-and-sell">
              <span className="material-icons camera-icon">camera_alt</span>
              Sell
            </Button>
          </div>
          <div className="login-button">
            <Button
              variant="outlined"
              color="secondary"
              onClick={this.showModal}
            >
              Log In
            </Button>
          </div>
          <span
            onClick={this.toggleDrawer}
            className="material-icons menu-icon"
          >
            menu
          </span>
          <Drawer
            anchor="right"
            open={this.state.drawerOpen}
            onClose={this.toggleDrawer}
            variant="temporary"
          >
            <div className="drawer-profile-section">
              <AccountCircleIcon onClick={this.showModal} id="logged-out-pic" />
              <h2 className="log-in" onClick={this.showModal}>
                Log in now
              </h2>
              <h4>You aren't logged in</h4>
            </div>
            <Divider />
            <List>
              {
                <ListItem
                  button
                  onClick={this.toggleDrawer}
                  component={Link}
                  to="/home"
                  key={"Discover"}
                >
                  <ListItemIcon>{<VisibilityIcon />}</ListItemIcon>
                  <ListItemText primary={"Discover"} />
                </ListItem>
              }
            </List>
          </Drawer>
        </div>
      );
    }
  };

  render() {
    return (
      <div className="my-navbar-container">
        <p>HELLLLOOOOO</p>
        {/* <LoginModal
          modalOpen={this.state.modalOpen}
          hideModal={this.hideModal}
        /> */}
        {/* <Link to="/home" className="home-logo">
          <h2>YoComics!</h2>
        </Link> */}
        <div className="search-bar-container">
          <input className="search-bar" placeholder="Search YoComics"></input>
        </div>
        {this.buildNavBar()}
      </div>
    );
  }
}

export default MyNavbar;
